CREATE TABLE IF NOT EXISTS wishList(id INTEGER PRIMARY KEY AUTOINCREMENT,title TEXT,title_english TEXT,authour TEXT);

CREATE TABLE IF NOT EXISTS books(id INTEGER PRIMARY KEY AUTOINCREMENT,book_id TEXT,title TEXT,title_english TEXT,authour TEXT,note TEXT,icon TEXT,category TEXT,fpublished_date TEXT,published_date TEXT,publisher TEXT,volume INTEGER,pages INTEGER,isbn INTEGER,price INTEGER,awards BLOB,is_read INTEGER,lend_name TEXT, lend_date TEXT,special_note TEXT);
